$(document).ready(function(){
    servidorca = {
        desAjaxIDDOMShow: function(objServer){ 
            $.ajax({type: objServer.tipo,url: objServer.servidor,data: objServer.datos,cache: false,success: function(dato){if($.trim(dato) === "1"){$("#"+objServer.idDOM).show("slow");}else{$("#"+objServer.error).show("slow");}},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});
        },
        desAjaxIDDOM: function(objServer){
            $.ajax({type: objServer.tipo,url: objServer.servidor,data: objServer.datos,cache: false,success: function(dato){if($.trim(dato) === "1"){$("#"+objServer.idDOM).hide("slow");}else{$("#"+objServer.error).show("slow");}},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});
        },
        desAjaxResultHTML: function(objServer){
            $.ajax({type: objServer.tipo,url: objServer.servidor,data: objServer.datos,cache: false,success: function(dato){$("#"+objServer.idDOM).fadeOut("fast",function(){$(this).html(dato).slideDown(10);});},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});
        },
        desAjaxResultHTMLProcc: function(objServer){
            $.ajax({type: objServer.tipo,url: objServer.servidor,data: objServer.datos,cache:false,beforeSend:function(){$("#"+objServer.idDOM).html("<center><img height='"+objServer.dim+"%' width='"+objServer.dim+"%' src='"+objServer.urlimg+"'></center>");},success: function(dato){$("#"+objServer.idDOM).fadeOut("fast",function(){$(this).html(dato).slideDown(10);});},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});
        }
    };
});

