/* global CKEDITOR */

function limpiarcampos(arreglo){for(var i = 0; i < arreglo.length; i++){$("#"+arreglo[i]).val("");}}
function errores(arreglo, borde, type, capa){
    var values = true;
    for(var i = 0; i < arreglo.length; i++){
        if(valCKE(arreglo[i])){
            if(capa){
                var cadena = arreglo[i].substring(3,arreglo[i].length);
                document.getElementById(cadena).style.border = "1px solid " + borde;
                if(type){$("#err"+arreglo[i]).hide("slow");}
            }
        }else{
            document.getElementById(arreglo[i]).style.border = "1px solid " + borde;
            if(type){$("#err"+arreglo[i]).hide("slow");}
        }
    }
    for(var i = 0; i < arreglo.length; i++){
        if(valCKE(arreglo[i])){
            if(capa){
                if(CKEDITOR.instances[arreglo[i]].getData() === ""){
                    var cadena = arreglo[i].substring(3,arreglo[i].length);
                    document.getElementById(cadena).style.border = "1px solid red";
                    if(type){$("#err"+arreglo[i]).hide("slow");}
                    values = false;
                }
            }
        }else{
            switch( $("#"+arreglo[i]).attr("type") ){
                case "email":
                {
                    if(!valEmail(document.getElementById(arreglo[i]).value)){
                        document.getElementById(arreglo[i]).style.border = "1px solid red";
                        if(type){$("#err"+arreglo[i]).show("slow");}
                        values = false;
                    }
                    if(document.getElementById(arreglo[i]).value === ""){
                        document.getElementById(arreglo[i]).style.border = "1px solid red";
                        if(type){$("#err"+arreglo[i]).show("slow");}
                        values = false;
                    }
                    break;
                }
                case "text":
                {
                    if(document.getElementById(arreglo[i]).value === ""){
                        document.getElementById(arreglo[i]).style.border = "1px solid red";
                        if(type){$("#err"+arreglo[i]).show("slow");}
                        values = false;
                    }
                    break;
                }
                default:
                    break;
            }
        }
    }
    return values;
}
function valCKE(cadenaver){var idDOM = cadenaver.split("");var cadena = "";var escape = false;for(var i = 0; i < idDOM.length; i++){cadena += idDOM[i];if(cadena==="CKE"){escape = true;break;}}return escape;}
function valEmail(valor){var re=/^[_a-z0-9-]+(.[_a-z0-9-]+)*@[a-z0-9-]+(.[a-z0-9-]+)*(.[a-z]{2,3})$/;if(!re.exec(valor)){return false;}else{return true;}}
function valLogicFechas(fechai, fechaf){var value = true;if(fechai > fechaf){value = false;}return value;}
