function desAjaxIDDOMShow(servidor, tipo, datos, idDOM, error){$.ajax({type: tipo,url: servidor,data: datos,cache: false,success: function(dato){if($.trim(dato) === "1"){$("#"+idDOM).show("slow");}else{$("#"+error).show("slow");}},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});}function desAjaxIDDOM(servidor, tipo, datos, idDOM, error){$.ajax({type: tipo,url: servidor,data: datos,cache: false,success: function(dato){if($.trim(dato) === "1"){$("#"+idDOM).hide("slow");}else{$("#"+error).show("slow");}},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});}function desAjaxResultHTML(servidor, tipo, datos, idDOM){$.ajax({type: tipo,url: servidor,data: datos,cache: false,success: function(dato){$("#"+idDOM).fadeOut("fast",function(){$(this).html(dato).slideDown(10);});},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});}function desAjaxResultHTMLProcc(servidor, tipo, datos, idDOM, dim, urlimg){$.ajax({type: tipo,url: servidor,data: datos,cache:false,beforeSend:function(){$("#"+idDOM).html("<center><img height='"+dim+"%' width='"+dim+"%' src='"+urlimg+"'></center>");},success: function(dato){$("#"+idDOM).fadeOut("fast",function(){$(this).html(dato).slideDown(10);});},error: function(XMLHttpRequest,textStatus,errorthrows){alert("Ocurri贸 un error: "+ errorthrows);}});}

